    func respondToDropdownSelection() {
        
        let chapter = "\(book) \(chapterIndex)"
        let numberOfCharacters = chapter.characters.count
        let attributedChapter = NSMutableAttributedString(string: chapter)
        attributedChapter.addAttribute(NSFontAttributeName, value: UIFont(name:"Helvetica",
            size:28.0)!, range: NSMakeRange(0,numberOfCharacters))
        myChapterView.attributedText = attributedChapter
        
        let allAnnotations = myMapView.annotations
        var shouldAddAnnotation = true
        var alreadyAddedAnnotation: MKAnnotation?
        
        for annotation in allAnnotations {
            if annotation.coordinate.latitude == pinCoordinatesArray[index][0] {
                shouldAddAnnotation = false
                alreadyAddedAnnotation = annotation
            }
        }
        
        if shouldAddAnnotation {
            
            let annotation = MKPointAnnotation()
            
            annotation.coordinate = CLLocationCoordinate2D(latitude: pinCoordinatesArray[index][0], longitude: pinCoordinatesArray[index][1])
            annotation.title = annotationTitlesArray[index]
            myMapView.addAnnotation(annotation)
            myMapView.selectAnnotation(annotation, animated: false)
            
        } else {
            
            myMapView.selectAnnotation(alreadyAddedAnnotation!, animated: false)
            
        }
        
        let coordinate = CLLocationCoordinate2D(latitude: centerCoordinatesArray[index][0], longitude: centerCoordinatesArray[index][1])
        
        myMapView.setRegion(MKCoordinateRegion(center: coordinate, span: MKCoordinateSpan(latitudeDelta: spansArray[index][0], longitudeDelta: spansArray[index][1])), animated: true)
        
        let newText = textArray[chapterIndex-1]
        
        let attributedString = NSMutableAttributedString(string: newText)
        let all = (newText as NSString).range(of: newText)
        attributedString.addAttribute(NSFontAttributeName, value: UIFont(name:"Helvetica-Light",
            size:16.0)!, range: all)
        
        for i in 1...numberOfChapters {
            let string_to_size = "\(book) \(i)"
            
            let range = (newText as NSString).range(of: string_to_size)
            
            attributedString.addAttribute(NSFontAttributeName, value: UIFont(name:"Helvetica",
                size:28.0)!, range: range)
            
        }
        
        for place in placesArray {
            let escapedString = place.addingPercentEscapes(using: String.Encoding.utf8)!
            print(escapedString)
            
            let range = (newText as NSString).range(of: escapedString)
            
            attributedString.addAttribute(NSFontAttributeName, value: UIFont(name:"Helvetica-Bold",
                size:16.0)!, range: range)
            attributedString.addAttribute(NSLinkAttributeName, value: escapedString, range: range)
        }
        
        myTextView.attributedText = attributedString
        
        if transitionTypeArray[index] == "Draw Route" {
            getDirections()
        }
        
        index += 1

    }
    
