    func mapView(_ mapView: MKMapView, rendererFor overlay: MKOverlay) -> MKOverlayRenderer {

        //if overlay is MKPolyline {
        //    let lineView = MKPolylineRenderer(overlay: overlay)
        //    lineView.strokeColor = UIColor.redColor()
            
        //    return lineView
        //}
        
        guard let polyline = overlay as? MKPolyline else {
            return MKOverlayRenderer()
        }
        
        if overlay is MKPolyline {
            let renderer = MKPolylineRenderer(polyline: polyline)
            renderer.lineWidth = 3.0
            renderer.alpha = 0.5
            renderer.strokeColor = UIColor.blue
            return renderer
        }
        
        return MKPolylineRenderer()
    }

    func mapView(_ mapView: MKMapView, viewFor annotation: MKAnnotation) -> MKAnnotationView? {
        
        let reuseId = "pin"
        
        var pinView = mapView.dequeueReusableAnnotationView(withIdentifier: reuseId) as? MKPinAnnotationView
        
        if pinView == nil {
            pinView = MKPinAnnotationView(annotation: annotation, reuseIdentifier: reuseId)
            pinView!.canShowCallout = true
            pinView!.pinTintColor = UIColor.red
        }
        else {
            pinView!.annotation = annotation
        }
        
        return pinView
    }
    
    func scrollToBottom() {
        let bottom = myTextView.contentSize.height
        myTextView.setContentOffset(CGPoint(x: 0, y: bottom - myTextView.frame.height), animated: true)
    }

    func scrollDown() {
        let amountToScroll = myTextView.contentSize.height - myTextView.frame.height
        print(amountToScroll)
        let alreadyScrolled = myTextView.contentOffset.y
        print(alreadyScrolled)
        let amountLeftToScroll = amountToScroll - alreadyScrolled
        print(amountLeftToScroll)
        let amountToOffset = amountLeftToScroll + myTextView.frame.height
        print(amountToOffset)
        myTextView.setContentOffset(CGPoint(x: 0, y: amountToOffset), animated: true)
    }
    
